// wir merken uns das fokussierte element global, um es nach ajax updates wieder zu fokussieren.
var focusedElementId = '';
// um die felder beim ajax-request nicht zu disablen kann dieses auf false gesetzt werden (benötigt fürs hga)
var disableFieldsOnAjaxRequest = true;

function saveFocusedElementId(evt) {
	focusedElementId = evt.target.id;
}

function refocus() {
	if (focusedElementId != '') {
		$('#'+focusedElementId).focus();

		if ($('#'+focusedElementId).is('input[type=text]')) {
			var strlen = $('#'+focusedElementId).val().length;
			setCursorPosition($('#'+focusedElementId), strlen);
		}
	}
}

function setCursorPosition(ctrl, pos){
	if(ctrl.get(0).setSelectionRange) {
		ctrl.focus();
		ctrl.get(0).setSelectionRange(pos,pos);
	}
	else if (ctrl.get(0).createTextRange) {
		var range = ctrl.get(0).createTextRange();
		range.collapse(true);
		range.moveEnd('character', pos);
		range.moveStart('character', pos);
		range.select();
	}
}

