/**
 * Gibt die X und Y Position fuer einen uebergebenen Container zurueck und
 * beachtet dabei auch das Riskiko wenn dieses außerhalb des sichtbaren Bereichs sind.
 *
 * @param object me
 * @return string
 */
function getXYInfoLayer(me) {
	referenz = $('#stage');
	scrollPos = {"left" : $(window).scrollLeft(), "top" : $(window).scrollTop()};

	mx = me.pageX + 'px';
	my = me.pageY + 'px';
	// Abfangen wenn Infolayer zu weit rechts raus geht
	if(me.pageX + currentInfoLayer.width() > referenz.width()) {
		diff = referenz.width() - (me.pageX + currentInfoLayer.width());
		mx = (me.pageX + diff - 20 - scrollPos.left) + 'px';
	}
	// Abfangen wenn Infolayer zu weit unten raus geht
	if(me.pageY + currentInfoLayer.height() > (referenz.height() - 50)) {
		diff = referenz.height() - (me.pageY + currentInfoLayer.height());
		my = (me.pageY - currentInfoLayer.height() - 70) + 'px';
	}
	return {top: my, left: mx};
}

var currentInfoLayer = null;
var hideTimeout = null;
var mouseOutSet = false;
/**
 * Setzt die Events die beobachtet werden sollen fuer die Infolayer.
 *
 * @param string linkId Id Des Links der beobachtet werden soll
 * @param string layerId Id des Containers der als Infolayer angezeigt werden soll.
 */
function setEventsInfoLayer(linkId, layerId) {

	if(mouseOutSet == false) {
		$(document).bind('mousemove',
			function(me) {
				try {
					currentInfoLayer.css(getXYInfoLayer(me));
				} catch(e) {}
			}
		);
		mouseOutSet = true;
	}

	$("#"+linkId).bind('mouseover', function(me) {
		try {
			window.clearTimeout(hideTimeout);
			currentInfoLayer.hide();
		} catch(e) {}
		currentInfoLayer = $("#"+layerId);
		currentInfoLayer.css(getXYInfoLayer(me));
		currentInfoLayer.show();
	});
	$("#"+linkId).bind('mouseout', function() {
		try {
			hideTimeout = window.setTimeout(function(){currentInfoLayer.hide();}, 100);
		}
		catch(e) {}
	});

}

/**
 * Verschickt eine Mail mit einer Ratgeberempfehlung.
 *
 * @param string actberater Der Aktuelle Berater
 */
function recommend(actberater){
	$('#recommendDiv').slideUp();
    var mail = $('#recommendEmail').val();
    var fromMail = $('#recommendFromEmail').val();
    var fromName = $('#recommendFromName').val();
    var recommendTextMsg = $('#recommendTextMsg').val();
	$.post( 'index.php',
		    {isAjax: "true", cmd: 'recommend', berater: actberater, email:mail, fromEmail:fromMail, fromName:fromName, recommendTextMsg:recommendTextMsg},
		    function(data, textStatus) {
				$('#recommendFeedback').html(data).slideDown();
				window.setTimeout("$('#recommendFeedback').slideUp();", 5000);
			},
			'html');
}

