/**
 * Initialisiert das Widget zu den Eingaben des Daches
 *
 *
 */
function initDacheingaben() {
    if($('#kompass').length == 0) {
        return false
    }


    $('#kompass').maphilight({fade: false});

    // Vorbelegung der Dachausrichtung
    if($('#dachausrichtung_hidden').val() != "") {
        // Wert ist vorausgewaehlt
        var wert = ', ' + $('#dachausrichtung_hidden').val();
        $('#mapDachausrichtung area').each(function() {
            if($(this).attr('href').search(wert) != -1) {
                $(this).trigger('click');
                try {
                    eval('(' + $(this).attr('href').match(/setGrad\('.*', -?[0-9]*\)/) + ')');
                }
                catch (e) {}
            }
        })
    }

    /**
     * DachNeigung
     */
    $('#dachneigung_checkbox').click(function() {
        // Auswahl Dachschraege loeschen
        $('#dachschraege_drag').trigger('resetDrag');
    });

    var foundIndex = 0;
    var fireAjax = false;
    var dragInterval = 5;
    var counter = 0;

    $('#dachschraege_drag')
        .bind('dragstart',function(){

            // Auswahl das unbekannt loeschen
            if($('#dachneigung_checkbox').attr('checked') == true) {
                fireAjax = true;
            }

            var data = $( this ).data('dragcircle');
            var parentElement = $( '#dachschraege' );
            if ( !data ) {
                data = {
                    $circle: $([]), $points: [],
                    halfHeight: $( this ).outerHeight()/2,
                    halfWidth: $( this ).outerWidth()/2
                };
                // Fuer jeden Punkt einen extra Span mit der Beschriftung erstellen und Positionieren
                var NumberOfPoints = 15;
                var leftPos = parentElement.width() / 2;
                for(var i = 0; i < NumberOfPoints; i++) {
                    var topPos = ((NumberOfPoints - i) * ((NumberOfPoints / 2) - 1)) ;

                    data.$points.push({top: topPos, left: leftPos});
                    parentElement.append( $('<span class="number" style="position: absolute;">'+(i*dragInterval)+'°</span>').css({ top: (topPos - 13), left: (leftPos + 15), display: 'none'  }) );
                };

                data.$circle = parentElement.find('.number');
                $( this ).data('dragcircle', data );
            }
        })
        .bind('drag',function( event ){
            var foundPoint = {top: 100000, left: 100000};
            var data = $( this ).data('dragcircle');
            var parentElement = $( '#dachschraege' );
            // Alten point ausblenden
            data.$circle.eq(foundIndex).hide();
            counter = 0;
            $.each( data.$points, function(){
                var abstandCurrent = Math.abs(foundPoint.top - (event.pageY - parentElement.offset().top)) + Math.abs(foundPoint.left - (event.pageX - parentElement.offset().left));
                var abstandPoint = Math.abs(this.top - (event.pageY - parentElement.offset().top)) + Math.abs(this.left - (event.pageX - parentElement.offset().left));
                if(abstandPoint < abstandCurrent) {
                    foundPoint = this;
                    foundIndex = counter;
                }
                counter++;
            });
            $( this ).css({
                top: foundPoint.top - data.halfHeight,
                left: foundPoint.left - data.halfWidth
            });
            var backgroundTop = -(foundIndex * parentElement.height());
            // neunen point ausblenden
            data.$circle.eq(foundIndex).show();
            $('#dachschraege').css('background-position', '0 '+backgroundTop+'px');
        })
        .bind('dragend',function(){
            $('#dachneigung_hidden').val(foundIndex * dragInterval);
            // Alle verstecken
            $( this ).data('dragcircle').$circle.hide();
            // Aktuellen Punkt anzeigen
            $( this ).data('dragcircle').$circle.eq(foundIndex).show();

            if(fireAjax == true) {
                ajaxBinder($('#dachneigung_checkbox'));
            }
        })
        .bind('moveToFoundPoint', function() {
            var data = $( this ).data('dragcircle');
            var parentElement = $( '#dachschraege' );
            var foundPoint = $( this ).data('dragcircle').$circle.eq(foundIndex).offset();

            $( this ).css({top: foundPoint.top - parentElement.offset().top});
            var backgroundTop = -(foundIndex * parentElement.height());
            $('#dachschraege').css('background-position', '0 '+backgroundTop+'px');
        })
        .bind('resetDrag',function(){
            // Reset Neigungsinput
            if($( this ).data('dragcircle')) {
                $( this ).data('dragcircle').$circle.hide();
                $('#dachschraege').css('background-position', '0 0px');
                $('#dachschraege_drag').css({top: '85px', left: '92px'});
            }
            $('#dachneigung_hidden').val('');
        }
    );


    // Vorbelegung der Dachneigung
    if($('#dachneigung_hidden').val() != "") {
        // initialisieren der Daten
        $('#dachschraege_drag').trigger("dragstart");

        foundIndex = ($('#dachneigung_hidden').val() / dragInterval);
        $('#dachschraege_drag').trigger('dragend');
        $('#dachschraege_drag').trigger('moveToFoundPoint');
    }

    return true;
}

$(document).ready(function () {
    // Initialisieren der Dachwidgets
    var initResult = initDacheingaben();
    if(initResult === false) {
        // Konnte nicht beim Seitenladen initialisiert werden, machen wir beim naechsten AjaxRequest
        var event = $("body").hasClass("secure")? "rebindFormElementsFinished" : "ajaxCompleted";
        $("body").bind(event, function() {
            initDacheingaben();
        });
    }

});

