$(document).ready(function() {
	// den nach-AJAX-Event binden und JS Constraints, weil die Hoehe sich danach aendern koennte
	$('body')
		.bind("rgfwPostAjax", sendMessage)
		.bind("constraintsReady", sendMessage)
		.bind("accordionReady", sendMessage)
		.bind("initIframeHeight", sendMessage);

	// on resize auch die Nachricht senden
	$(window).bind("resize", sendMessage);

	// die Initiale-Nachricht senden wenn die Seite geoffnet wurde
	sendMessage();
});

// Beim laden des kompletten Inhalts (also inklusive Bilder) auch noch mal die Hoehe setzen
$(window).on("load", sendMessage);

// die Id des Frames speichern, damit dieser identifizierbar ist.
var rgfwFrameId = null;
/**
 * Wenn es noch keine id gibt, wird diese angefordert.
 * Ansonsten wird die id mit der aktuellen hoehe als Nachricht versendet.
 * Struktur: id:Zahl|Hoehe => id:4|666
 * @returns {undefined}
 */
function sendMessage() {
	if(window == window.parent) {
		return;
	}

	// id noch nicht bekannt,also anfordern
	if (rgfwFrameId === null) {
		window.parent.postMessage("idNeeded", "*");
	}
	// id ist bekannt, also die hoehe uebermitteln
	else {
		window.parent.postMessage("id:" + rgfwFrameId + "|" + $("body").height(),'*');
	}
}
/**
 * Holt sich die uebergebene id aus der Nachricht und versendet die Nachricht mit aktueller Hoehe
 * @param {Object} event
 * @returns {undefined}
 */
function listener(event){
	rgfwFrameId = parseInt(event.data);
	sendMessage();
}
// wenn window die funktion addEventListener besitzt, dann diese nutzen
if (window.addEventListener) {
	addEventListener("message", listener, false);
}
// ansonsten attachEvent nutzen
else {
	attachEvent("onmessage", listener);
}



