/**
 * Initialisiert im Nutzerdaten-Formular das Widget EskLabelOutputChange
 *
 * @author Tassilo Margraf
 */
function initEskLabelOutputChange() {

	// Felder ein- und ausblenden
	$('.eskDisabledOutputChange').each(function () {
		// Wenn Fehler vorhanden, dann oeffnen
		if ($(this).find('.error').length > 0) {
			$(this).find('ul').slideDown(function () {
				$(this).find('.changeLink').removeClass('fieldsClosed');
				// Richtigen Text anzeigen
				$(this).parent().find('.linkTextOpen').hide();
				$(this).parent().find('.linkTextClose').show();
			});
		}
		// Wenn Klasse groupOpen vorhanden, dann oeffnen
		else if ($(this).find('.fieldsOpen').length > 0) {
			$(this).find('ul').slideDown(function () {
				// Klasse fieldsOpen dient nur der Initialisierung des Formulars
				// und kann entfernt werden
				$(this).find('.changeLink').removeClass('fieldsOpen');
				// Richtigen Text anzeigen
				$(this).parent().find('.linkTextOpen').hide();
				$(this).parent().find('.linkTextClose').show();
			});
		}
		// Wenn keine Fehler oder Klasse fieldsClosed vorhanden,
		// dann geschlossen lassen
		else {
			$(this).find('ul').css('display', 'none');
			$(this).find('.changeLink').addClass('fieldsClosed');
			// Richtigen Text anzeigen
			$(this).find('.linkTextOpen').show();
			$(this).find('.linkTextClose').hide();
		}
	});
	// Klick-Event zum Ein- und Ausblenden der Felder
	$('.eskDisabledOutputChange').find('.changeLink').click(function (event) {

		// Defaultverhalten des Browsers, d.h. Ausfuehren des Links, unterbinden
		event.preventDefault();

		// Felder ein-/ausblenden
		$(this).parent().find('ul.changeFields').toggle();

		// Richtigen Text anzeigen
		if ($(this).hasClass('fieldsClosed')) {
			$(this).find('.linkTextOpen').hide()
			$(this).find('.linkTextClose').show();
		}
		else {
			$(this).find('.linkTextOpen').show();
			$(this).find('.linkTextClose').hide();
		}

		// Klasse abwechseln hinzufuegen/entfernen
		$(this).toggleClass('fieldsClosed');

		// Wenn geschlossen, dann Felder leeren
		if ($(this).hasClass('fieldsClosed')) {
			$(this).parent().find('ul input').val('');
		}

		return false;

	});

}


