/**
 * Alle Felder und Buttons eines Formulars auf enabled setzen
 *
 * @author Tassilo Margraf
 */
function enableFields(form) {

	form.find('input, select, button').each(function () {

		var enableField = true;

		for (var i in disabledFields) {
			if (disabledFields[i] == $(this).attr('name')) {
				enableField = false;
				break;
			}
		}

		if (enableField == true) {
			$(this).removeAttr("disabled");
		}
		else {
			// zur Sicherheit das Attribut noch mal setzen, da es bei ausgetauschten
			// Ajaxfeldern sonst nichts da waere
			$(this).attr("disabled", true);
		}

	});

}


/**
 * Alle Felder und Buttons eines Formulars auf disabled setzen
 *
 * @author Tassilo Margraf
 */
function disableFields(form) {

	// Array fuer die Zwischenspeicherung, welche Felder disabled wurden
	disabledFields = [];

	form.find('input, select, button').each(function () {
		if ($(this).attr("disabled") == "disabled" || $(this).attr("disabled") == true) {
			disabledFields.push($(this).attr('name'));
		}
		$(this).attr("disabled", "disabled");
	});

}
