function isBrowserInternetexplorer() {
    var sAgent = window.navigator.userAgent;
    var Idx = sAgent.indexOf("MSIE");

    // Wenn es IE ist, dann version nummer ermitteln.
    if (Idx > 0) {
        return true;
    }
    // Ab IE 11 nach geupdateten user agent pruefen.
    else{
        if (!!navigator.userAgent.match(/Trident\/7\./)) {
            return true;
        }
        //It is not IE
        else {
            return false;
        }
    }
}

