/**
 * Replaces a single Highchart chart with a png. The svg will be hidden
 *
 * @param {HighchartsAdapter} chart The Highchart/Chart Object that should be replaced
 * @param {Function} callback Function that is called after a image is loaded
 */
function replaceChartWithPng(chart, callback) {
    if(!chart) {
        callback.call();
        return;
    }
	// get width and height of the svg
    var render_width = chart.chartWidth;
    var render_height = chart.chartHeight;

    // Get the cart's SVG code
    var svg = chart.getSVG({
        exporting: {
            sourceWidth: chart.chartWidth,
            sourceHeight: chart.chartHeight
        }
    });

    /*
     * Wenn InternetExplorer - der macht natuerlich wieder alles voll uebel.
     * Quellen sind
     *      @see: http://jsfiddle.net/6bxqbaeb/1/ und
     *      @see: http://willkoehler.net/2014/11/07/client-side-solution-for-downloading-highcharts-charts-as-images.html
     */
    if(isBrowserInternetexplorer() === true){

        // Filtert Text den canvg() nicht kann: aus ...xlink:href="/gfx/esk/diagramme/ereignis_fahne.png" ... wird ... xlink:href=""...
        var replacement = '';
        svg = str_replace('xlink:href="(.+?)"', 'xlink:href="' + replacement + '"', svg);

        // Ersetze in der erzeugten Grafik angezeigte '&nbsp;' durch ein Leerzeichen.
        svg = str_replace("&amp;nbsp;", ' ', svg);

        // Create a canvas
        var canvas = document.createElement('canvas');
        canvas.height = render_height;
        canvas.width = render_width;
        document.body.appendChild(canvas);

        window.onbeforeunload = null;
        canvg(canvas, svg);

        replaceOneChartsWithImage(canvas, canvas.toDataURL("image/png"), callback, chart);

    }
    // Wenn ein anderer der ueblichen Browser - die nicht so ueble Fehler erzeugen.
    else{

        // Ersetze in der erzeugten Grafik angezeigte '&nbsp;' durch ein Leerzeichen.
        svg = str_replace("&amp;nbsp;", ' ', svg);

        // Create a canvas
        var canvas = document.createElement('canvas');
        canvas.height = render_height;
        canvas.width = render_width;
        document.body.appendChild(canvas);

        // Create an image and draw the SVG onto the canvas
        var image = new Image;
        image.onload = function() {
            // draw the canvas
            canvas.getContext('2d').drawImage(this, 0, 0, render_width, render_height);
            replaceOneChartsWithImage(canvas, canvas.toDataURL("image/png"), callback, chart);
        };
        var encodedData = window.btoa(unescape(encodeURIComponent(svg)));
        image.src = 'data:image/svg+xml;base64,' + encodedData;

    }

}

/**
 * Relaced one SvgHighchartsDiagramm with an image url. The ReverseMethod is @see: resetReplacedCharts().
 *
 * @param canvas
 * @param {string} imageUrl like an image date url string.
 * @param {Function} callback
 * @param {HighchartsAdapter} chart
 */
function replaceOneChartsWithImage(canvas, imageUrl, callback, chart){
    // hide the chart and add a new image as png
    $(chart.container)
        .find("svg, .highcharts-data-labels")
        .parent()
        .hide()
        .parent()
        .append("<img class='chart-replace' src='" + imageUrl + "'/>")
    ;

    // remove the canvase, we don't need this any longer
    document.body.removeChild(canvas);
    // call the callback after everything is done
    callback.call();
}

/**
 * Resets all the replaced pngs to the old svg
 */
function resetReplacedCharts() {
    $("svg, .highcharts-data-labels").parent().show();
    $("img.chart-replace").remove();
}

/**
 * Replaces all the svg Charts on the current page with a png
 *
 * @param {Function} callback optional callback that is called after all the charts are replaced
 */
function replaceAllChartsWithPng(callback) {
    if(Highcharts && Highcharts.charts.length > 0) {
        var imagesLoaded = 0;

        for(var i=0; i<Highcharts.charts.length; i++) {
            replaceChartWithPng(Highcharts.charts[i], function() {
                // Bilder die gerendert wurden hoch zaehlen
                imagesLoaded++;
                if(imagesLoaded == Highcharts.charts.length) {
                    // all images loaded => trigger an event on the body after all charts are replaced
                    $("body").trigger("chartsReplaced");

                    if(callback) {
                        callback.call();
                    }
                }
            });
        }
    }
	else {
		// keine Bilder, Event und callback direkt feuern
		$("body").trigger("chartsReplaced");

		if(callback) {
			callback.call();
		}
	}
}


