			var elementLeft = rect.left + scrollLeft + borderLeft;
			var elementTop = rect.top + scrollTop + borderTop;

			x = evt.pageX - elementLeft;
			y = evt.pageY - elementTop;

			var containerBreiteMitRand = $.getNumberCleanedFromUnit(canvasJo.css('width'));
			var containerInhaltsBreiteAlsoOhneRand = containerBreiteMitRand - $.getNumberCleanedFromUnit(canvasJo.css('border-left-width')) - $.getNumberCleanedFromUnit(canvasJo.css('border-right-width'));

