/**
 * Wird ausgefuert wenn die Auswahl einer SelectBox veraendert wurde.
 * Entwernt alle Optionen aller SelectBoxen und baut diese vollstaendig neu.
 * Leerstring-Options werden dabei uebernommen, andere kommen aus rgfwData.options.
 * @returns {undefined}
 */
function updateOptions() {
	var hideElement = "hideElement";
	if (typeof rgfwData !== "undefined" && typeof rgfwData.options !== "undefined") {
		var elementId = "";
		var value = "";
		var option;
		var field;
		$(".jsoptions select").each(function(index, element) {
			value = $("#"+element.id).val();
			if (typeof value !== "string") {
				value = "";
			}
			elementId = extractOverloaded(element.id);
			if (typeof rgfwData.options[elementId] !== "undefined") {
				field = $("#"+elementId).first();
				for (var i = 0; i < rgfwData.options[elementId].length; i++) {
					option = rgfwData.options[elementId][i];
					if (eval(option.if) === false) {
						$(field).find("option[value=\""+option.value+"\"]").addClass(hideElement);
						if (value === option.value) {
							value = "";
						}
					}
					else {
						$(field).find("option[value=\""+option.value+"\"]").removeClass(hideElement);
					}
				}
				if ($("#"+element.id+" option[value=\""+value+"\"]:not(."+hideElement+")").length === 0 && $("#"+element.id+" option:not(."+hideElement+")").length > 0) {
					value = $("#"+element.id+" option:not(."+hideElement+")")[0].value;
				}
				$("#"+element.id).val(value);
			}
		});
		$('body').trigger("constraintsReady");
	}
};
/**
 * Wird ausgefuert wenn Inhalt eines Inputs oder einer Select sich geaendert hat.
 * Blendet die DIVs mit den Radios aus, wenn diese nicht angezeigt werden, mit hilfe von hideElement-Klasse.
 * @returns {undefined}
 */
function updateRadios() {
	if (typeof rgfwData !== "undefined" && typeof rgfwData.options !== "undefined") {
		$(".SimpleRadio.jsoptions .radio-wrapper-wrapper").each(function(index, element) {
			if (typeof rgfwData.options[element.id] !== "undefined") {
				var value = getValue(element.id);
				var hideElement = "disableElement";
				var checked = "checked";
				var disabled = "disabled";
				$("#"+element.id+" > .radio-wrapper").removeClass(hideElement);
				$("#"+element.id+" input").removeAttr(checked).attr(disabled, disabled);
				var option;
				for (var i = 0; i < rgfwData.options[element.id].length; i++) {
					option = rgfwData.options[element.id][i];
					if (eval(option["if"]) === false) {
						$("#"+element.id+" input[value="+option["value"]+"]").parent().addClass(hideElement);
					}
					else  {
						$("#"+element.id+" input[value="+option["value"]+"]").removeAttr(disabled);
					}
				}
				$("#"+element.id+" .radio-wrapper:not(.disableElement) input[value='"+value+"']").attr(checked, checked);
			}
		});
		$('body').trigger("constraintsReady");
	}
};
/**
 * Kriegt die Property, gibt den Selector fuer alle Selects und Inputs unter dem <li> der Property.
 * @param {String} field
 * @returns {String}
 */
function getSelector(field) {
	field = "#formrow"+field;
	return field+" input, "+field+" select";
};
/**
 * Prueft, ob das Feld ueberladen ist, wenn ja, dann wird nur der Teil bis zu dem "_overloaded_" zurueckgegeben
 * @param {String} field
 * @returns {String}
 */
function extractOverloaded(field) {
	var overloadedStart = field.indexOf("_overloaded_");
	if (overloadedStart > 0) {
		return field.substr(0, overloadedStart);
	}
	else {
		return field;
	}
};
/**
 * Kriegt die Property, gibt den Wert der Property zurueck.
 * Bei Select die ausgeqaehlten Wert, bei Input die User-Eingabe.
 * @see getSelector()
 * @param {String} field
 * @returns {String}
 */
function getValue(field) {
	if ($(getSelector(field)).is("select") || $(getSelector(field)).is(":text")) {
		return $(getSelector(field)).val();
	}
	if ($(getSelector(field)).filter(":checked").length > 0) {
		return $(getSelector(field)).filter(":checked").first().val();
	}
	return $(getSelector(field)).first().val();
};
/**
 * Initialisiert ein Einzelnes Feld.
 * Blendet diesen aus und disabled die Eingabe-Elemente, oder macht genau dies rueckgaengig.
 * @param {String} field
 * @param {Boolean} show
 * @returns {undefined}
 */
function initField(field, show) {
	var hideElement = "hideElement";
	var disabled = "disabled";
	field = "#formrow"+field;
	if (show === true) {
		$(field).removeClass(hideElement).find("input, select").removeAttr(disabled);
	}
	else if ($(field).hasClass(hideElement) === false) {
		$(field).addClass(hideElement).find("input, select").attr(disabled, disabled);
	}
};
